mapper.select:
select a.id,a.store_id,a.portrait,a.name,a.info,a.base_price,p.price,p.point_rate,p.buy_type,cat.point_rate basePointRate,a.address from sale_act_price p join sale_act a on p.act_id=a.id LEFT join sys_act_category cat on a.category_id=cat.id where p.delete_flag=0 and p.forbid_flag=0 and p.review_status=1
and a.store_id = #{storeId}
and a.act_type_id = #{actTypeId}
and now() between p.start_time and p.end_time
and now() > p.end_time
order by a.display_weight desc,p.id desc

select count(*) from sale_act_price p join sale_act a on p.act_id=a.id LEFT join sys_act_category cat on a.category_id=cat.id where p.delete_flag=0 and p.forbid_flag=0 and p.review_status=1
and a.store_id = #{storeId}
and a.act_type_id = #{actTypeId}
and now() between p.start_time and p.end_time
and now() > p.end_time
