sql.where:
p.delete_flag=0 and ss.project_id=#{projectId}

    and p.name like #{actName}


    and ss.name like #{storeName}

mapper.select:
select a.id,a.store_id,a.portrait,a.name,a.base_price,p.price,p.point_rate,p.buy_type,cat.point_rate basePointRate,a.address,store.name storeName,store.service_phone from sale_act_price p join sale_act a on p.act_id=a.id join sys_act_category cat on a.category_id=cat.id join sys_store store on a.store_id=store.id where p.delete_flag=0 and p.forbid_flag=0 and p.review_status=1
and store.project_id = #{projectId}
and a.store_id = #{storeId}
and now() between p.start_time and p.end_time order by a.display_weight desc,p.id desc
