sql.where:
a.delete_flag=0 and p.store_id=#{storeId}

    and a.review_status = #{reviewStatus}


    and a.name like #{nameLike}

mapper.select:
select count(*) from sale_product_price where forbid_flag=false
and id !=#{id}
and ((start_time >= #{startTime} and start_time < #{endTime}) or(end_time > #{startTime} and start_time <= #{endTime}))
