mapper.sql:
ccc.delete_flag = 0

    cc.name LIKE #{couponName}


    cc.type = #{couponType}


    ccl.name LIKE #{couponLaunchName}


    ccc.code = #{code}


    ccc.client_id = #{clientId}

mapper.select:
SELECT
,
cc.name AS coupon_name, cc.type AS coupon_type, ccl.name AS coupon_launch_name
private String couponName;
private Byte couponType;
private String couponLaunchName;
FROM card_coupon_code ccc
INNER JOIN card_coupon_launch ccl ON ccl.id = ccc.launch_id AND ccl.delete_flag = 0
INNER JOIN card_coupon cc ON cc.id = ccl.coupon_id AND cc.delete_flag = 0



ORDER BY ccc.create_time DESC
