mapper.sql:
ccc.delete_flag = 0

    AND cc.project_id = #{projectId}



        AND ccc.code = #{filter.code}


        AND c.tel = #{filter.tel}


        AND cc.type = #{filter.couponType}


        AND cc.name LIKE #{filter.couponName}


        AND ccl.name LIKE #{filter.couponLaunchName}


        AND cts.name LIKE #{filter.thirdPartyName}


        AND cc.thirdparty_shop_id = #{filter.thirdPartyShopId}

mapper.select:
select cc.name AS coupon_name, cc.type AS coupon_type, cc.amount, cc.subtract_amount, ccl.name AS launch_name, ccl.type AS launch_type, ccc.id AS coupon_code_id, ccc.code, ccc.use_status, ss.name AS shop_name, ccl.effective_start_time, ccl.effective_end_time from card_coupon_code ccc inner join card_coupon_launch ccl on ccl.id = ccc.launch_id and ccl.delete_flag = 0 inner join card_coupon cc on cc.id = ccl.coupon_id and cc.delete_flag = 0 and cc.`type` != 3 inner join card_coupon_store_scope ccss on ccss.coupon_id = cc.id and ccss.delete_flag = 0 inner join sys_shop ss on ss.id = ccss.store_id and ss.code = #{shopCode} and ss.project_id = #{projectId} and ss.delete_flag = 0 inner join card_coupon_vip_scope ccvs on ccvs.coupon_id = cc.id and ccvs.delete_flag = 0 inner join client c on c.id = ccc.client_id and c.delete_flag = 0 and c.vip_card_grade = ccvs.vip_type
and c.tel = #{tel}
AND c.vip_code = #{vipCode}
AND ccc.delete_flag = 0

SELECT ccc.id FROM card_coupon_code ccc INNER JOIN ( SELECT launch_start_time, launch_end_time FROM card_coupon_launch WHERE now() > launch_start_time AND now() < launch_end_time and type = 1 and confirm_flag = 1 and forbid_flag = 0 and review_status = 1 and delete_flag = 0 ) launch_time ON ccc.receive_time > launch_time.launch_start_time AND ccc.receive_time < launch_time.launch_end_time INNER JOIN card_coupon_launch ccl ON ccl.id = ccc.launch_id AND ccl.delete_flag = 0 AND ccl.type = 1
ccc.client_id = #{clientId} AND ccc.use_status != 3 AND ccc.belong = -1 AND ccc.delete_flag = 0
limit 0,#{num}
