mapper.sql:
ccl.delete_flag = 0
AND cc.name LIKE #{couponName}
AND cc.type = #{couponType}
AND ccl.name LIKE #{name}
AND ccl.type = #{type}
-- 投放状态，0.审核未通过 1. 未审核，2 未投放，3 未开始，4 投放中，5 投放已过期，6 投放已中止
AND ccl.review_status = 3
AND ccl.review_status = 0
AND ccl.review_status = 1 AND ccl.confirm_flag = 0
AND ccl.review_status = 1 AND ccl.confirm_flag = 1 AND now() <= ccl.launch_start_time
AND ccl.review_status = 1 AND ccl.confirm_flag = 1 AND ccl.forbid_flag = 0 AND now() > ccl.launch_start_time AND now() < IFNULL(ccl.forbid_time,ccl.launch_end_time)
AND ccl.review_status = 1 AND ccl.confirm_flag = 1 AND ccl.forbid_flag = 0 AND now() > IFNULL(ccl.forbid_time,ccl.launch_end_time)
AND ccl.forbid_flag = 1

mapper.select:
select ss.id AS id, , ss.name AS shop_name, ccss.ratio, count(DISTINCT psd.client_id) AS effective_client_num, count(ccc.id) AS effective_coupon_num, count(ccc.id) * cc.subtract_amount * ccss.ratio AS shop_ratio_amount, (count(ccc.id) * cc.subtract_amount) - (count(ccc.id) * cc.subtract_amount * ccss.ratio) AS landlord_ratio_amount from card_coupon_launch ccl INNER JOIN card_coupon cc ON cc.id = ccl.coupon_id AND cc.delete_flag = 0 INNER JOIN card_coupon_code ccc ON ccc.launch_id = ccl.id AND ccc.delete_flag = 0 AND ccc.use_status = 2 INNER JOIN card_coupon_store_scope ccss ON ccss.coupon_id = cc.id AND ccss.delete_flag = 0 INNER JOIN pos_sale_detail psd ON psd.shop_id = ccss.store_id AND psd.order_code = ccc.order_code INNER JOIN sys_shop ss ON ss.id = psd.shop_id AND ss.delete_flag = 0 WHERE ccl.id = #{launchId}
AND cc.name LIKE #{shopName}
AND ccl.confirm_flag = 1 AND ccl.review_status = 1 AND ccl.delete_flag = 0 GROUP BY psd.shop_id ORDER BY count(ccc.id) DESC

SELECT count(*) FROM ( select ccl.id from card_coupon_launch ccl INNER JOIN card_coupon cc ON cc.id = ccl.coupon_id AND cc.delete_flag = 0 INNER JOIN card_coupon_code ccc ON ccc.launch_id = ccl.id AND ccc.delete_flag = 0 AND ccc.use_status = 2 INNER JOIN card_coupon_store_scope ccss ON ccss.coupon_id = cc.id AND ccss.delete_flag = 0 INNER JOIN pos_sale_detail psd ON psd.shop_id = ccss.store_id AND psd.order_code = ccc.order_code INNER JOIN sys_shop ss ON ss.id = psd.shop_id AND ss.delete_flag = 0 WHERE ccl.id = #{launchId}
AND cc.name LIKE #{shopName}
AND ccl.confirm_flag = 1 AND ccl.review_status = 1 AND ccl.delete_flag = 0 GROUP BY psd.shop_id ) temp

select , , ccl.launch_num - IFNULL(ccc.receive_num,0) AS remain_num, cc.name AS coupon_name, cc.type AS coupon_type, cc.portrait, cc.info, cc.amount, cc.subtract_amount from card_coupon_launch ccl INNER JOIN card_coupon cc ON cc.id = ccl.coupon_id INNER JOIN card_coupon_vip_scope ccvs ON ccvs.coupon_id = cc.id INNER JOIN client c ON c.vip_card_grade = ccvs.vip_type LEFT JOIN ( select id, launch_id, count(*) AS receive_num from card_coupon_code WHERE delete_flag = 0 GROUP BY launch_id ) ccc ON ccc.launch_id = ccl.id WHERE c.id = #{clientId} AND cc.project_id = #{projectId}
AND cc.type = #{couponType}
AND now() > ccl.launch_start_time AND now() < ccl.launch_end_time AND ccl.type = 2 AND ccl.review_status = 1 AND ccl.confirm_flag = 1 AND ccl.forbid_flag = 0 AND ccl.delete_flag = 0 ORDER BY cc.display_weight DESC, ccl.create_time DESC

select COUNT(*) from card_coupon_launch ccl INNER JOIN card_coupon cc ON cc.id = ccl.coupon_id INNER JOIN card_coupon_vip_scope ccvs ON ccvs.coupon_id = cc.id INNER JOIN client c ON c.vip_card_grade = ccvs.vip_type LEFT JOIN ( select id, launch_id, count(*) AS receive_num from card_coupon_code WHERE delete_flag = 0 GROUP BY launch_id ) ccc ON ccc.launch_id = ccl.id WHERE c.id = #{clientId} AND cc.project_id = #{projectId}
AND cc.type = #{couponType}
AND now() > ccl.launch_start_time AND now() < ccl.launch_end_time AND ccl.type = 2 AND ccl.review_status = 1 AND ccl.confirm_flag = 1 AND ccl.forbid_flag = 0 AND ccl.delete_flag = 0

select COUNT(*) from card_coupon_launch ccl INNER JOIN card_coupon cc ON cc.id = ccl.coupon_id LEFT JOIN ( select id, launch_id, count(*) AS receive_num from card_coupon_code WHERE delete_flag = 0 GROUP BY launch_id ) ccc ON ccc.launch_id = ccl.id where cc.project_id = #{projectId} AND now() > ccl.launch_start_time AND now() < ccl.launch_end_time
AND cc.type = #{couponType}
AND ccl.type = 2 AND ccl.review_status = 1 AND ccl.confirm_flag = 1 AND ccl.forbid_flag = 0 AND ccl.delete_flag = 0
