mapper.sql:
cc.delete_flag = 0 AND cc.project_id = #{projectId}

AND cc.name LIKE #{name}


AND cc.type = #{type}

mapper.select:
SELECT , SUM(launch_num) AS sum_launch_num, cts.name AS thirdparty_shop_name, CASE WHEN !ISNULL(SUM(ccl.launch_num)) THEN cc.num - SUM(ccl.launch_num) END AS available_num FROM ( SELECT * FROM card_coupon WHERE delete_flag = 0
) cc LEFT JOIN card_coupon_launch ccl ON cc.id = ccl.coupon_id and ccl.delete_flag = 0 and ccl.confirm_flag = 1 AND ccl.review_status = 1 LEFT JOIN card_third_party_shop cts ON cts.id = cc.thirdparty_shop_id AND cts.delete_flag = 0
GROUP BY cc.id ORDER BY cc.create_time DESC
