mapper.select:
select

from sale_product_price p
left join sale_product a on a.id = p.product_id
left join sys_product_category c on a.category_id=c.id
where

    p.special_price_flag = #{specialPriceFlag}


    and  a.store_id = #{storeId}


    and  a.designer_id = #{designerId}

and p.forbid_flag=0 and p.delete_flag=0 and p.review_status=1 and p.start_time<=now() and p.end_time>=now()
order by a.display_weight desc,a.id desc

select
from sale_product_price p left join sale_product a on a.id = p.product_id left join sys_product_category c on a.category_id=c.id left join sys_store ss on a.store_id = ss.id where ss.project_id = #{projectId}
and p.special_price_flag = #{specialPriceFlag}
and p.forbid_flag=0 and p.delete_flag=0 and p.review_status=1 and p.start_time<=now() and p.end_time>=now() order by a.display_weight desc,a.id desc
